declare variable PR smallint;
declare variable CAT smallint;
declare variable term smallint;
begin
    s_all_0=0; s_all_1=0; s_cel=0; s_2_0=0; s_2_1=0; s_vk_0=0; s_vk_1=0; s_wo_0=0; s_wo_1=0; s_ing=0; s_2v=0;
