                    end
                end else begin
                    if ((:term=1) or (:term=2)) then begin
                        s_2_1=s_2_1+1;
                    end else begin
                        if (:konk=0) then s_all_1=s_all_1+1;
                        if (:konk=1) then s_wo_1=s_wo_1+1;
                        if (:konk=2) then s_vk_1=s_vk_1+1;
                    end
                end
            end
        end
    end

  suspend;
end^


